definition.script:
node ('master'){
   stage 'Checkout'
   git 'https://github.com/haroonzone/hello-dropwizard'
   stage 'Build application and Run Unit Test'
   def mvnHome = '/usr'
   sh "${mvnHome}/bin/mvn clean package"
    try {
        sh 'mvn test -B'
    } catch(err) {
        step([$class: 'JUnitResultArchiver', testResults: '**/target/surefire-reports/TEST-*.xml'])
        throw err
    }


   stage 'Build Docker image'

   def image = docker.build('java:8-jdk', '.')

   stage 'Acceptance Tests'
   image.withRun('-p 8181:8080') {c ->
        sh "${mvnHome}/bin/mvn verify"
   }

//   stage 'Push image'

//   docker.withRegistry("http://172.28.1.3:5000", "docker-registry") {
//      image.tag("latest", true)
//      image.push()
//   }

}
